import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Particles from 'react-tsparticles';
import { loadSlim } from 'tsparticles-slim';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Home from './pages/Home';
import './App.css';

function App() {
  const particlesInit = async (engine) => {
    await loadSlim(engine);
  };

  return (
    <Router>
      <div className="App">
        {/* --- Particle Background --- */}
        {/* Layer 1: Shooting Stars */}
        <Particles
          id="shooting-stars"
          init={particlesInit}
          options={{
            fullScreen: { enable: true, zIndex: -2 },
            background: { color: "#000000" },
            particles: {
              number: { value: 150, density: { enable: true, area: 800 } },
              color: { value: "#ffffff" },
              shape: { type: "circle" },
              opacity: { value: 0.5 },
              size: { value: 1 },
              move: {
                enable: true,
                speed: 0.5,
                direction: "right",
                straight: false,
                random: true,
                outModes: "out",
                trail: {
                  enable: true,
                  length: 5,
                  fillColor: "#000000"
                }
              }
            }
          }}
        />

        {/* Layer 2: Black Hole (center swirl) */}
        <Particles
          id="black-hole"
          init={particlesInit}
          options={{
            fullScreen: { enable: true, zIndex: -1 },
            particles: {
              number: { value: 50 },
              color: { value: "#ffffff" },
              shape: { type: "circle" },
              opacity: { value: 0.6 },
              size: { value: 2 },
              move: {
                enable: true,
                speed: 1,
                direction: "none",
                random: false,
                straight: false,
                outModes: "out",
                attract: {
                  enable: true,
                  rotateX: 3000,
                  rotateY: 3000
                }
              }
            }
          }}
        />

        {/* --- Page Components --- */}
        <Navbar />
        <Hero />
        <div className="app-container">
          <Routes>
            <Route path="/" element={<Home />} />
            {/* Future: <Route path="/scientist/:id" element={<ScientistDetail />} /> */}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
