import React, { useEffect, useState } from 'react';
import ScientistCard from './ScientistCard';
import data from '../assets/scientists.json';
import { Container, Row, Col } from 'react-bootstrap';

function ScientistGrid() {
  return (
    <Container>
      <Row>
        {data.map((sci) => (
          <Col key={sci.id} md={4}>
            <ScientistCard
              name={sci.name}
              period={sci.period}
              contribution={sci.contribution}
              image={`/images/${sci.image}`} // assumes in /public/images
            />
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default ScientistGrid;
