import React from 'react';
import './styles/Timeline.css';
// import { Link } from 'react-router-dom'; // optional, for future routing

function TimelineItem({ name, image, side, id }) {
  return (
    <div className={`timeline-item ${side}`}>
      <div className="timeline-dot" />
      <Link to={`/scientist/${id}`} className="timeline-content">
        <img src={`/images/${image}`} alt={name} className="timeline-img" />
        <p className="timeline-name">{name}</p>
      </Link>
    </div>
  );
}

export default TimelineItem;
