import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Home from './pages/Home';

import Particles from 'react-tsparticles';
import { loadStarsPreset } from 'tsparticles-preset-stars'; // ✅ Correct preset

function App() {
  const particlesInit = async (engine) => {
    await loadStarsPreset(engine); // Load stars effect
  };

  return (
    <Router>
      <div className="App">
        {/* Starry Background */}
        <Particles
  id="tsparticles"
  init={particlesInit}
  options={{
    fullScreen: { enable: true, zIndex: -1 },
    background: {
      color: "#000000", // dark night sky
    },
    particles: {
      number: {
        value: 100,
        density: { enable: true, area: 800 },
      },
      color: { value: "#ffffff" },
      shape: { type: "circle" },
      opacity: {
        value: 0.8,
      },
      size: {
        value: 1.5,
      },
      move: {
        enable: true,
        speed: 1,
        direction: "right", // mimic star drift
        straight: false,
        random: true,
        outModes: "out",
        trail: {
          enable: true,
          length: 5,
          fillColor: "#000000"
        }
      }
    },
    interactivity: {
      events: {
        onHover: { enable: true, mode: "repulse" },
        onClick: { enable: true, mode: "push" },
      },
      modes: {
        push: { quantity: 4 },
        repulse: { distance: 50, duration: 0.4 },
      },
    },
    detectRetina: true,
  }}
/>

        <Navbar />
        <Hero />
        <div className="app-container">
          <Routes>
            <Route path="/" element={<Home />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
